import React,{useState} from "react"


var userIsRegistered = true;

function Input(props) {

  const [text,setText] = useState("hello")
  const [mouseover,setMouseover] = useState(false)
  const [name,setName] = useState("")

  function change(event) {
  setName(event.target.value)
  }
  function submitted() {
    setText(`Thank you ${name}, you are registered.`)
  }
  function black() {
    setMouseover(true)
  }
  function white() {
    setMouseover(false)
  }
    return <div>
        <h1>{text}</h1>
        <input type="text" placeholder="username" onChange={change} />
      <input type="password" placeholder="password" />
      <input type="password" placeholder="confirm password" />
      <button type="submit" onMouseOver={black} onMouseOut={white} style={{backgroundColor: mouseover ? "black" : "white"}} onClick={submitted}>Register</button>
    </div>
}

export default Input;
